import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { FoodItemService } from '../service/fooditem.service';
import { FoodCataloguePage } from 'src/app/Shared/models/FoodCataloguePage';
import { FoodItem } from 'src/app/Shared/models/FoodItem';

@Component({
  selector: 'app-food-catalogue',
  templateUrl: './food-catalogue.component.html',
  styleUrls: ['./food-catalogue.component.css']
})
export class FoodCatalogueComponent {

  restaurantId: number;
  foodItemResponse: FoodCataloguePage;
  foodItemCart: FoodItem[] = [];
  orderSummary: FoodCataloguePage;


  constructor(private route: ActivatedRoute, private foodItemService: FoodItemService, private router: Router) {
  }

  ngOnInit() {

    this.route.paramMap.subscribe(params => {
      this.restaurantId = +params.get('id');
    });

    this.getFoodItemsByRestaurant(this.restaurantId);

  }

  getFoodItemsByRestaurant(restaurant: number) {
    this.foodItemService.getFoodItemsByRestaurant(restaurant).subscribe(
      data => {
        this.foodItemResponse = data;
      }
    )
  }

  increment(food: any) {
    food.quantity++;
    const index = this.foodItemCart.findIndex(item => item.id === food.id);
    if (index === -1) {
      // If record does not exist, add it to the array
      this.foodItemCart.push(food);
    } else {
      // If record exists, update it in the array
      this.foodItemCart[index] = food;
    }
  }

  decrement(food: any) {
    if (food.quantity > 0) {
      food.quantity--;

      const index = this.foodItemCart.findIndex(item => item.id === food.id);
      if (this.foodItemCart[index].quantity == 0) {
        this.foodItemCart.splice(index, 1);
      } else {
        // If record exists, update it in the array
        this.foodItemCart[index] = food;
      }

    }
  }

  onCheckOut() {
    this.foodItemCart;
    this.orderSummary = {
      foodItemsList: [],
      restaurant: null
    }
    this.orderSummary.foodItemsList = this.foodItemCart;
    this.orderSummary.restaurant = this.foodItemResponse.restaurant;
    this.router.navigate(['/orderSummary'], { queryParams: { data: JSON.stringify(this.orderSummary) } });
  }




}

<div class="restaurant-details">
    <div class="restaurant-header">
        <h1>{{foodItemResponse.restaurant.name}}</h1>
        <button class="checkout-button" (click)="onCheckOut()">
            <i class="fas fa-shopping-cart"></i>
            Checkout
        </button>
    </div>
    <p>{{foodItemResponse.restaurant.restaurantDescription}}</p>

    <h2>Food Menu</h2>
    <div class="food-list">
        <div class="food-item" *ngFor="let food of foodItemResponse.foodItemsList">
            <div class="food-details">
                <div class="food-name">{{food.itemName}}</div>
                <div class="food-price">&#x20b9; {{food.price}}</div>
            </div>
            <div class="food-quantity">
                <button class="minus-button" *ngIf="food.quantity > 0" (click)="decrement(food)">-</button>
                <div class="quantity-label">{{food.quantity}}</div>
                <button class="plus-button" (click)="increment(food)">+</button>
            </div>
        </div>
    </div>

</div>
